  temp() {}

  render() {
    return (
      <div>
        <p>{this.fullName}</p>
        <Button variant="contained" color="primary" onClick={temp}>
          Start New Game
        </Button>
        <p>
          <span>Top Ten:</span>
        </p>
        {/*<p>
          {
              gameResult.topTen.map((score, index) => {
                  return <span key={'score_' + index} className="top-score">{score}</span>
              })
          }
      </p>*/}
        <p>{message}</p>
        <p>
          You did {gameResult.moves} move and you have completed{" "}
          {gameResult.turns} turns.
        </p>
        <div className="board">
          {gameResult.board
            ? gameResult.board.map((row, indexRow) => {
                return (
                  <div
                    key={"container_" + keyContainer++}
                    className="board-row"
                  >
                    {row.map((column, indexColumn) => {
                      return (
                        <Square
                          key={"square_" + keySquare++}
                          row={indexRow}
                          column={indexColumn}
                          data={column}
                          onClick={onClickPlayGame.bind(this)}
                        ></Square>
                      );
                    })}
                  </div>
                );
              })
            : ""}
        </div>
      </div>
    );
  }
}

export default Board;
